sys_script_fix.script:
// var userid = '3117a3e597534110cf6f797e6253afae'; //Test dev 1
var groupId = '2f85134b97938110cf6f797e6253af4c'; // CryptoTrackerDevs
var myScopeId = 'd733440497430110cf6f797e6253afa0'; //sys_scope

// var myPermissionSetId = '043a115e679112006cc2956c33415a36'; // All Metadata

var permissionList = getPermList();
// gs.debug(permissionList)

for(var i = 0; i < permissionList.length; i++) {
    var myPermissionSetId = permissionList[i];
    var mgr = DelegatedDevRoleManagerPP.forScopeAndPermissionSet(myScopeId, myPermissionSetId);

    // mgr.allocatePermissionToUser(userid);
    mgr.allocatePermissionToGroup(groupId);
}

function getPermList() {
    var permList = [];
    var permgr = new GlideRecord('sys_development_permission_set');
    permgr.query();
    while(permgr.next()) 
        permList.push(permgr.getUniqueValue());
    return permList;
}
